from selenium import webdriver
from selenium.webdriver.common.action_chains import ActionChains
from selenium.webdriver.common.keys import Keys
import time, random
import json


def flowgpt_info():
    driver = webdriver.Chrome(r"C:\Program Files\Google\Chrome\Application\chromedriver.exe")  # Chrome浏览器
    url = "https://flowgpt.com/"
    dict_info = {}
    driver.get(url)
    driver.implicitly_wait(10)
    for i in range(100):
        ActionChains(driver).key_down(Keys.PAGE_DOWN).key_up(Keys.PAGE_DOWN).perform()
        time.sleep(random.random() * 3)
    for n in range(550):
        items = driver.find_element('xpath', '//*[@id="scrollableDiv"]/div[2]/div[2]/div/div[2]/div/div[{}]/div'.format(n+1))
        try:
            items = items.text.split('\n')
        except:
            items = []
        dict_info.update({n+1: items})
    print(json.dumps(dict_info))

    return dict_info


